import React, { useState, useEffect } from 'react';
import { Button } from '../../Button.js';
import { Link } from 'react-router-dom';
import '../../MainPage/Styles/Navbar.css';

function Navbar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

  return (
    <>
      <nav id="" className='navbar'>
        <div className='navbar-container'>
          <a href="/homepage" className='navbar-logo' onClick={closeMobileMenu}>
            CoHelper
            <i className="fas fa-comments-dollar"></i>
          </a>
          
          
            <div className='navbar-logo'>
            <i class="fas fa-bell"></i>
            </div>
            
            
            {/* <li className='navbar-logo'>
            <i class="fas fa-comments"></i>
            </li>
            <li className='nav-item'>
              <Link to="/" className="nav-links1" onClick={closeMobileMenu}>Выйти</Link>
            </li> */}
          
          <div className='into-btn'><Link to='/sign-in'>{button && <Button buttonStyle='btn--outline'>Выйти</Button>}</Link></div>
        </div>
      </nav>
    </>
  );
}

export default Navbar;
