import React from 'react';
import validate from './validateInfo';
import useForm from './useForm';
import '../Styles/Form.css';

const FormSignup = ({ submitForm }) => {
  const { handleChange, handleSubmit, values, errors } = useForm(
    submitForm,
    validate
  );

  return (
    <div className='form-content-right'>
      <form onSubmit={handleSubmit} className='form' noValidate>
        <h1>
          Найдите бизнес-партнера сегодня! 
          Создайте аккаунт своей компании, заполнив информацию ниже.
        </h1>
        <div className='form-inputs'>
          <label className='form-label'>Название компании</label>
          <input
            className='form-input'
            type='text'
            name='username'
            placeholder='Введите название компании'
            value={values.username}
            onChange={handleChange}
          />
          {errors.username && <p>{errors.username}</p>}
        </div>
        <div className='form-inputs'>
          <label className='form-label'>Чем занимается Ваша компания?</label>
          <select
            className='form-input'
            name='activity'
          >
            <option selected disabled>Выберите род деятельности</option>
            <option>Фермер</option>
          </select>
        </div>
        <div className='form-inputs'>
          <label className='form-label'>Местоположение компании</label>
          <input
            className='form-input'
            type='text'
            name='city'
            placeholder='Введите название населенного пункта'
            value={values.city}
            onChange={handleChange}
            // Validation like {errors.email && <p>{errors.email}</p>}
          />
        </div>
        <div className='form-inputs'>
          <label className='form-label'>Email</label>
          <input
            className='form-input'
            type='email'
            name='email'
            placeholder='email@address.com'
            value={values.email}
            onChange={handleChange}
          />
          {errors.email && <p>{errors.email}</p>}
        </div>
        <div className='form-inputs'>
          <label className='form-label'>Password</label>
          <input
            className='form-input'
            type='password'
            name='password'
            placeholder='Enter your password'
            value={values.password}
            onChange={handleChange}
          />
          {errors.password && <p>{errors.password}</p>}
        </div>
        <div className='form-inputs'>
          <label className='form-label'>Confirm Password</label>
          <input
            className='form-input'
            type='password'
            name='password2'
            placeholder='Confirm your password'
            value={values.password2}
            onChange={handleChange}
          />
          {errors.password2 && <p>{errors.password2}</p>}
        </div>
        <button className='form-input-btn' type='submit'>
          Sign up
        </button>
        <span className='form-input-login'>
          Already have an account? Login <a href='#'>here</a>
        </span>
      </form>
    </div>
  );
};

export default FormSignup;
