import React, { useState, useEffect } from "react";
import Navbar from "./Elements/Navbar.js";
import Cards from "./Elements/Cards.js";
import firebase from "firebase";

function HomePage() {
  const userToken = localStorage.getItem("token");
  const db = firebase.firestore();
  const [info, setInfo] = useState([]);
  useEffect(() => {
    async function getInfo() {
      const querySnapshot = await db
        .collection("users")
        .where("token", "!=", userToken)
        .get();
      querySnapshot.forEach((doc) => {
        let info_find=""
        if (doc.data().findFor.advertising===true){
            info_find+="совместную рекламу; ";
        }
        if (doc.data().findFor.clients===true){
            info_find+="заказчиков; ";
        }
        if (doc.data().findFor.investors===true){
            info_find+="инвесторов; ";
        }
        if (doc.data().findFor.providers===true){
            info_find+="рекламу; ";
        }
        doc.data().findFor=info_find;
        console.log(doc.data().findFor)
        info.push(doc.data());
        setInfo([...info]);
      });
    }
    getInfo();
  }, []);

  return (
    <>
      <Navbar />
      <>
        <Cards info={info} />
      </>
    </>
  );
}

export default HomePage;
